describe('GET /api/health', () => {
  it('returns status ok and a valid ISO 8601 timestamp', async () => {
    // Import the route module to test the handler logic directly
    const { Route } = await import('./health')

    // Access the server handler
    const handler = (Route.options as any).server.handlers.GET

    const response = await handler()

    expect(response).toBeInstanceOf(Response)
    expect(response.status).toBe(200)

    const body = await response.json()
    expect(body.status).toBe('ok')
    expect(body.timestamp).toBeDefined()

    // Verify timestamp is valid ISO 8601
    const parsed = new Date(body.timestamp)
    expect(parsed.toISOString()).toBe(body.timestamp)
  })
})

