import { describe, it, expect } from 'vitest'

describe('GET /api/health', () => {
  it('returns status ok and a valid ISO 8601 timestamp', async () => {
    const { Route } = await import('./health')
    const handler = (Route.options as any).server.handlers.GET

    const response = await handler()

    expect(response).toBeInstanceOf(Response)
    expect(response.status).toBe(200)

    const body = await response.json()
    expect(body.status).toBe('ok')
    expect(body.timestamp).toBeDefined()

    // Verify timestamp is valid ISO 8601
    const parsed = new Date(body.timestamp)
    expect(parsed.toISOString()).toBe(body.timestamp)
  })

  it('returns uptime_seconds as a non-negative integer', async () => {
    const { Route } = await import('./health')
    const handler = (Route.options as any).server.handlers.GET

    const response = await handler()
    const body = await response.json()

    expect(typeof body.uptime_seconds).toBe('number')
    expect(body.uptime_seconds).toBeGreaterThanOrEqual(0)
    expect(Number.isInteger(body.uptime_seconds)).toBe(true)
  })

  it('shows increasing uptime between requests', async () => {
    const { Route } = await import('./health')
    const handler = (Route.options as any).server.handlers.GET

    const response1 = await handler()
    const body1 = await response1.json()

    // Wait briefly to allow uptime to potentially increase
    await new Promise((resolve) => setTimeout(resolve, 50))

    const response2 = await handler()
    const body2 = await response2.json()

    expect(body2.uptime_seconds).toBeGreaterThanOrEqual(body1.uptime_seconds)
  })
})

