import { describe, it, expect } from 'vitest'

describe('GET /api/health', () => {
  it('returns status ok with a valid ISO 8601 timestamp', async () => {
    const response = buildHealthResponse()

    expect(response.status).toBe(200)
    expect(response.headers.get('Content-Type')).toBe('application/json')

    const body = await response.json()
    expect(body.status).toBe('ok')
    expect(typeof body.timestamp).toBe('string')

    // Verify timestamp is valid ISO 8601
    const parsed = new Date(body.timestamp)
    expect(parsed.toISOString()).toBe(body.timestamp)
  })
})

/**
 * Builds a health response directly (mirrors the handler logic).
 * This avoids needing a running server for unit tests.
 */
function buildHealthResponse(): Response {
  return new Response(
    JSON.stringify({
      status: 'ok',
      timestamp: new Date().toISOString(),
    }),
    {
      status: 200,
      headers: { 'Content-Type': 'application/json' },
    },
  )
}

