type HealthHandler = () => Response
type HealthRouteOptions = {
  server: {
    handlers: {
      GET: HealthHandler
    }
  }
}

function getHealthHandler(route: { options: unknown }): HealthHandler {
  return (route.options as HealthRouteOptions).server.handlers.GET
}

describe('GET /api/health', () => {
  it('returns status ok with a valid ISO timestamp', async () => {
    const { Route } = await import('./health')
    const handler = getHealthHandler(Route)
    const response = handler()

    expect(response).toBeInstanceOf(Response)
    expect(response.status).toBe(200)

    const body = await response.json()
    expect(body.status).toBe('ok')
    expect(typeof body.timestamp).toBe('string')

    // Verify ISO 8601 format
    const parsed = new Date(body.timestamp)
    expect(parsed.toISOString()).toBe(body.timestamp)
  })

  it('includes uptime_seconds as a non-negative integer', async () => {
    const { Route } = await import('./health')
    const handler = getHealthHandler(Route)
    const response = handler()
    const body = await response.json()

    expect(typeof body.uptime_seconds).toBe('number')
    expect(body.uptime_seconds).toBeGreaterThanOrEqual(0)
    expect(Number.isInteger(body.uptime_seconds)).toBe(true)
  })

  it('shows increasing uptime between requests', async () => {
    const { Route } = await import('./health')
    const handler = getHealthHandler(Route)

    const first = await handler().json()
    // Wait briefly to allow uptime to potentially increase
    await new Promise((resolve) => setTimeout(resolve, 10))
    const second = await handler().json()

    expect(second.uptime_seconds).toBeGreaterThanOrEqual(first.uptime_seconds)
  })
})

