import { describe, it, expect } from 'vitest'
import { getHealthResponse } from './health'

describe('GET /api/health', () => {
  it('returns status ok with a valid ISO 8601 timestamp', () => {
    const result = getHealthResponse()

    expect(result.status).toBe('ok')
    expect(result.timestamp).toBeDefined()

    // Verify timestamp is valid ISO 8601
    const parsed = new Date(result.timestamp)
    expect(parsed.toISOString()).toBe(result.timestamp)
  })

  it('returns a recent timestamp', () => {
    const before = new Date().toISOString()
    const result = getHealthResponse()
    const after = new Date().toISOString()

    expect(result.timestamp >= before).toBe(true)
    expect(result.timestamp <= after).toBe(true)
  })

  it('includes uptime_seconds as a non-negative integer', () => {
    const result = getHealthResponse()

    expect(typeof result.uptime_seconds).toBe('number')
    expect(result.uptime_seconds).toBeGreaterThanOrEqual(0)
    expect(Number.isInteger(result.uptime_seconds)).toBe(true)
  })

  it('uptime_seconds increases over time', async () => {
    const first = getHealthResponse()

    // Wait briefly to let uptime increase
    await new Promise((resolve) => setTimeout(resolve, 1100))

    const second = getHealthResponse()

    expect(second.uptime_seconds).toBeGreaterThanOrEqual(
      first.uptime_seconds + 1,
    )
  })
})

