 1→import type { Request, Response } from 'express'
 2→import * as UserModel from '../models/users.js'
 3→
 4→export function listUsers(req: Request, res: Response): void {
 5→  const users = UserModel.findAll()
 6→  res.json({ users })
 7→}
 8→
 9→export function getUser(req: Request, res: Response): void {
10→  const id = parseInt(req.params.id, 10)
11→  const user = UserModel.findById(id)
12→  if (!user) {
13→    res.status(404).json({ error: 'User not found' })
14→    return
15→  }
16→  res.json({ user })
17→}
18→
19→export function createUser(req: Request, res: Response): void {
20→  const { name, email } = req.body as { name?: string; email?: string }
21→  if (!name || !email) {
22→    res.status(400).json({ error: 'name and email are required' })
23→    return
24→  }
25→  const user = UserModel.create(name, email)
26→  res.status(201).json({ user })
27→}
28→

 1→export interface User {
 2→  id: number
 3→  name: string
 4→  email: string
 5→  createdAt: string
 6→}
 7→
 8→const users: User[] = [
 9→  { id: 1, name: 'Alice', email: 'alice@example.com', createdAt: '2024-01-01T00:00:00Z' },
10→  { id: 2, name: 'Bob', email: 'bob@example.com', createdAt: '2024-01-02T00:00:00Z' },
11→]
12→
13→let nextId = 3
14→
15→export function findAll(): User[] {
16→  return [...users]
17→}
18→
19→export function findById(id: number): User | undefined {
20→  return users.find((u) => u.id === id)
21→}
22→
23→export function create(name: string, email: string): User {
24→  const user: User = { id: nextId++, name, email, createdAt: new Date().toISOString() }
25→  users.push(user)
26→  return user
27→}
28→

